import React, {FC, useContext} from 'react'
import styled from 'styled-components'
import {pxToRem, device} from './util'

const red = '#B42732'
const yellow = '#FFB703'

const Body = styled.div`
  font-family: Nunito;
  display: flex;
  flex-direction: column;
  width: 100vw;
`

const MainHero = styled.section`
  background-color: ${red};
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`
const MainTitle = styled.h1`
  font-size: ${pxToRem(80)};
  @media ${device.laptop} {
    font-size: ${pxToRem(98)};
  }
  @media ${device.desktop} {
    font-size: ${pxToRem(118)};
  }
  margin-top: 20px;
  line-height: 161px;
`

const App: FC = props => (
  <Body>
    <MainHero>
      <MainTitle>Knoa ROI Calculator</MainTitle>
    </MainHero>
  </Body>
)

export default App
