import React, {FC, useState} from 'react'
import {
  Body,
  Input,
  MainHero,
  MainHeroSecondaryWrapper,
  MainTitle,
  MoneySaved,
  MoneySavedSecondaryText,
  MoneySavedWrapper,
  SecondaryTitle,
} from './App.styles'
import {annualBenefits, toMillion} from './util'

const App: FC = props => {
  const [employees, setEmployees] = useState<number | null>(1000)
  return (
    <Body>
      <MainHero px="28">
        <MainTitle>Knoa ROI Calculator</MainTitle>
        <SecondaryTitle>
          Input the number of employees to calculate the business impact Knoa
          UEM can have on your organization.
        </SecondaryTitle>
        <MainHeroSecondaryWrapper>
          <SecondaryTitle>
            If you have{' '}
            <Input
              onChange={event => {
                const {
                  target: {value},
                } = event
                if (value == '') setEmployees(null)
                setEmployees(parseInt(event.target.value))
              }}
              value={employees ?? 0}
              width={employees === null ? 1 : employees.toString().length}
            />
            employees, Knoa could save your company
          </SecondaryTitle>
          <MoneySavedWrapper>
            <MoneySaved>{toMillion(annualBenefits(1, employees))}</MoneySaved>
            <MoneySavedSecondaryText>In year one</MoneySavedSecondaryText>
          </MoneySavedWrapper>
        </MainHeroSecondaryWrapper>
      </MainHero>
    </Body>
  )
}

export default App
