import React, {FC, useContext} from 'react'
import {EventContext, Styled} from 'direflow-component'
import styled from 'styled-components'

const pxToRem = (pixels: number) => (pixels * 1) / 15

const red = '#B42732'
const yellow = '#FFB703'

const Body = styled.div`
  font-family: Nunito;
  display: flex;
  flex-direction: column;
`

const MainHero = styled.section`
  background-color: ${red};
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`
const MainTitle = styled.h1`
  font-size: ${pxToRem(118)};
  margin-top: 20px;
  line-height: 161px;
`

const App: FC = props => (
  <Body>
    <MainHero>
      <MainTitle>Knoa ROI Calculator</MainTitle>
    </MainHero>
  </Body>
)

export default App
