import styled, {css} from 'styled-components'
import {device, pxToRem} from './util'

const offWhite = '#fafafa'
const red = '#B42732'
const yellow = '#FFB703'

export const Body = styled.div`
  font-family: Nunito;
  display: flex;
  flex-direction: column;
  width: 100vw;
`

export const MainHero = styled.section`
  background-color: ${red};
  color: white;
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
  align-items: center;
`
const title = css`
  font-size: ${pxToRem(60)};
  font-weight: bold;
  @media ${device.mobileL} {
    font-size: ${pxToRem(60)};
  }
  @media ${device.tablet} {
    font-size: ${pxToRem(80)};
  }
  @media ${device.laptop} {
    font-size: ${pxToRem(98)};
  }
  @media ${device.desktop} {
    font-size: ${pxToRem(118)};
  }
`
const highlight = css`
  font-size: ${pxToRem(120)};
  font-weight: bold;
  @media ${device.mobileL} {
    font-size: ${pxToRem(140)};
  }
  @media ${device.tablet} {
    font-size: ${pxToRem(150)};
  }
  @media ${device.laptop} {
    font-size: ${pxToRem(160)};
  }
  @media ${device.desktop} {
    font-size: ${pxToRem(170)};
  }
`
export const MainTitle = styled.h1`
  ${title}
  margin-bottom: 0px;
  margin-top: 20px;
`

export const SecondaryTitle = styled.h2`
  max-width: 100%;
  @media ${device.laptop} {
    max-width: 50%;
  }
  font-size: ${pxToRem(30)};
  line-height: 40px;
  margin-top: 4px;
  @media ${device.laptop} {
    font-size: ${pxToRem(40)};
    line-height: 60px;
  }
  @media ${device.desktop} {
    font-size: ${pxToRem(53)};
    line-height: 70px;
  }
  font-weight: normal;
  color: ${offWhite};
`

export const MainHeroSecondaryWrapper = styled.div`
  display: flex;
  flex-direction: column;
  @media ${device.laptop} {
    flex-direction: row;
  }
`

export const MoneySavedWrapper = styled.div`
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
`
export const MoneySaved = styled.span`
  ${highlight}
  max-width: 100%;
  @media ${device.laptop} {
    max-width: 50%;
  }
  font-weight: bold;
`

export const MoneySavedSecondaryText = styled.span`
  font-weight: bold;
  font-size: ${pxToRem(20)};
  @media ${device.mobileL} {
    font-size: ${pxToRem(30)};
  }
  @media ${device.laptop} {
    font-size: ${pxToRem(40)};
  }
  @media ${device.desktop} {
    font-size: ${pxToRem(50)};
  }
`

export const Input = styled.input<{width: number}>`
  appearance: none;
  -webkit-appearance: none;
  --moz-appearance: none;
  background-color: transparent;
  width: ${props => props.width}ch;
  border-top: none;
  border-left: none;
  border-right: none;
  color: white;
  outline: none;
  border-bottom: 3px solid ${yellow};
  ${title}
`
