import React, {FC, useState} from 'react'
import {
  Body,
  Input,
  MainHero,
  MainHeroSecondaryWrapper,
  MainTitle,
  MoneySaved,
  MoneySavedSecondaryText,
  MoneySavedWrapper,
  SecondaryTitle,
  YearsHero,
} from './App.styles'
import {annualBenefits, toMillion} from './util'

const App: FC = props => {
  const [employees, setEmployees] = useState<number | null>(1000)
  const [years, setYears] = useState<number | null>(3)
  return (
    <Body>
      <MainHero>
        <MainTitle>Knoa ROI Calculator</MainTitle>
        <SecondaryTitle>
          Input the number of employees to calculate the business impact Knoa
          UEM can have on your organization.
        </SecondaryTitle>
        <MainHeroSecondaryWrapper>
          <SecondaryTitle>
            If you have{' '}
            <Input
              color="white"
              onChange={event => {
                const {
                  target: {value},
                } = event
                if (value === '') setEmployees(null)
                else setEmployees(parseInt(event.target.value))
              }}
              value={employees ?? 0}
              width={employees === null ? 1 : employees.toString().length}
            />
            employees, Knoa could save your company
          </SecondaryTitle>
          <MoneySavedWrapper>
            <MoneySaved>
              {toMillion(annualBenefits(1, employees ?? 0))}
            </MoneySaved>
            <MoneySavedSecondaryText>In year one</MoneySavedSecondaryText>
          </MoneySavedWrapper>
        </MainHeroSecondaryWrapper>
      </MainHero>
      <YearsHero>
        <MoneySavedSecondaryText>or</MoneySavedSecondaryText>
        <MoneySaved style={{marginLeft: '2em'}}>
          {toMillion(annualBenefits(years ?? 0, employees ?? 0))}
        </MoneySaved>
      </YearsHero>
    </Body>
  )
}

export default App
