const size = {
  mobileS: '320px',
  mobileM: '375px',
  mobileL: '425px',
  tablet: '768px',
  laptop: '1024px',
  laptopL: '1440px',
  desktop: '2560px'
};
export const device = Object.fromEntries(Object.entries(size).map(([key, value]) => [key, `(min-width: ${value})`]));
export const pxToRem = pixels => `${pixels / 15}rem`;
export const productivityBenefits = (years, employees) => 309.5 * years * employees;
export const itStaffProductivityBenefits = (years, employees) => 76.1 * employees * years;
export const riskMitigation = (years, employees) => 55.1 * employees * years;
export const annualBenefits = (years, employees) => [productivityBenefits, itStaffProductivityBenefits, riskMitigation].reduce((acc, f) => acc + f(years, employees), 0);
export const numberFormat = money => `$${money < 100000 ? `${(money / 1000).toFixed(0)}K` : `${(money / 1000000).toFixed(1)}M`}`;
export const toCommaSeparated = int => int.toFixed(0).replace(/\B(?=(\d{3})+(?!\d))/g, ',');
