export const device = Object.fromEntries(Object.entries(size).map(([key, value]) => [key, `(min-width: ${value})`]));
export const pxToRem = pixels => `${pixels / 15}rem`;
export const productivityBenefits = (years = 1) => 309500 * years;
export const itStaffProductivityBenefits = (years = 1) => 76100 * years;
export const riskMitigation = (years = 1) => 55100 * years;
export const annualBenefits = (years = 1) => [productivityBenefits, itStaffProductivityBenefits, riskMitigation].map(f => f(years)).reduce((acc, n) => acc + n, 0);
