const size = {
  mobileS: '320px',
  mobileM: '375px',
  mobileL: '425px',
  tablet: '768px',
  laptop: '1024px',
  laptopL: '1440px',
  desktop: '2560px'
};
export const device = Object.fromEntries(Object.entries(size).map(([key, value]) => [key, `(min-width: ${value})`]));
export const pxToRem = pixels => `${pixels / 15}rem`;
export const productivityBenefits = (years = 1, employees = 1000) => 309.5 * years * employees;
export const itStaffProductivityBenefits = (years = 1, employees = 1000) => 76.1 * employees * years;
export const riskMitigation = (years = 1, employees = 1000) => 55.1 * employees * years;
export const annualBenefits = (years = 1, employees = 1000) => [productivityBenefits, itStaffProductivityBenefits, riskMitigation].map(f => f(years, employees)).reduce((acc, n) => acc + n, 0);
