import React, {ChangeEventHandler, FC, useState} from 'react'
import {pipe} from 'fp-ts/function'
import {
  Benefit,
  BenefitList,
  BenefitSection,
  Body,
  Input,
  MainHero,
  MainHeroSecondaryWrapper,
  MainTitle,
  MoneySaved,
  MoneySavedSecondaryText,
  MoneySavedWrapper,
  SecondaryTitle,
  textDark,
  YearsHero,
} from './App.styles'
import {annualBenefits, productivityBenefits, toMillion} from './util'

const toCommaSeparated = (int: number): string =>
  int.toFixed(0).replace(/\B(?=(\d{3})+(?!\d))/g, ',')

type UseNullableNumber = (
  init: number
) => [number, ChangeEventHandler<HTMLInputElement>]
const useNumber: UseNullableNumber = init => {
  const [value, setValue] = useState(init)
  return [
    value,
    event => {
      const {
        target: {value},
      } = event
      if (value === '') setValue(0)
      else setValue(parseInt(value))
    },
  ]
}
const App: FC = props => {
  const [employees, setEmployees] = useNumber(1000)
  const [years, setYears] = useNumber(3)
  return (
    <Body>
      <MainHero>
        <MainTitle>Knoa ROI Calculator</MainTitle>
        <SecondaryTitle>
          Input the number of employees to calculate the business impact Knoa
          UEM can have on your organization.
        </SecondaryTitle>
        <MainHeroSecondaryWrapper>
          <SecondaryTitle>
            If you have{' '}
            <Input
              color="white"
              onChange={setEmployees}
              value={employees}
              width={employees.toString().length}
            />
            employees, Knoa could save your company
          </SecondaryTitle>
          <MoneySavedWrapper>
            <MoneySaved>{toMillion(annualBenefits(1, employees))}</MoneySaved>
            <MoneySavedSecondaryText>In year one</MoneySavedSecondaryText>
          </MoneySavedWrapper>
        </MainHeroSecondaryWrapper>
      </MainHero>
      <YearsHero>
        <MoneySavedSecondaryText>or</MoneySavedSecondaryText>
        <MoneySaved>
          {pipe(annualBenefits(years, employees), toMillion)}
        </MoneySaved>
        <MoneySavedSecondaryText>over</MoneySavedSecondaryText>
        <Input
          color={textDark}
          onChange={setYears}
          value={years}
          width={years.toString().length}
        />
        <MoneySavedSecondaryText>Years</MoneySavedSecondaryText>
      </YearsHero>
      <BenefitSection>
        <BenefitList>
          {[
            <span>
              In just {years} {years === 1 ? 'year' : 'years'} of using Knoa UEM
              you will see{' '}
              <em>
                $
                {pipe(productivityBenefits(years, employees), toCommaSeparated)}
              </em>{' '}
              in business productivity gains
            </span>,
            <span>
              IT Staff Productivity Benefits and Infrastructure cost reductions
              will also present some nice savings, around <em>$228,300</em> for
              your company
            </span>,
            <span>
              Lastly, mitigated risks and employee productivity benefits are
              expected to generate savings of <em>$165,300</em> a year in a
              company of your size
            </span>,
          ].map(element => (
            <Benefit key={element.key?.toString()}>{element}</Benefit>
          ))}
        </BenefitList>
      </BenefitSection>
    </Body>
  )
}

export default App
